import { useContext, useEffect, useState } from 'react';
import {Context} from './App';

function Products() {
    const {dataStatus} = useContext(Context);
    const [productsHtml, setProductsHtml] = useState('');

    const show = function() {
        // 1) забрать данные из локасторэдж
        // 2) parse in object
        // 3) пройтись по данным через map
        // 3.1 функиця мэп должна вернуть ли каждого продукта с данными 
        let data = JSON.parse(localStorage.getItem('data'));
        let list = data.map((item, index) => {
            return (
                <li key={index} className="list_item">
                    <img src={item.image}></img>
                    <div className="list_item_info">
                        <p className="info_title">{item.title}</p>
                        <p className="info_category">{item.category}</p>
                        <p className="info_price">${item.price}</p>
                        <button className="info_add_btn">+</button>
                    </div>
                </li>
            )
        })

        setProductsHtml(list)
    }

    useEffect(_ => {
        if(dataStatus) {
            show()
        }
    }, [dataStatus]);

    return (
        <ul className="list">
            {productsHtml}
        </ul>
    )
}

export default Products;
