import React, {useState, useEffect, useContext} from 'react';


function Cart() {
    const {setCartNumber} = useContext(Context);
    const [cartState, setCartState] = useState(getCookie('data') ? true : false);

    let items = JSON.parse(localStorage.getItem('data'));

    function getCookie(data) {
        let itemsIds = document.cookie.match(new RegExp(
          "(?:^|; )" + data.replace(/([\.$?*|{}\(\)\[\]\\\/\+^])/g, '\\$1') + "=([^;]*)"
        ));
       
        return itemsIds ? decodeURIComponent(itemsIds[1]) : undefined;
    }

    let itemsIds = getCookie('data').split(',');

    let cartItems = [];

    items.forEach(item => {
        for(let i = 0; i < itemsIds.length; i++) {
            if(item.id == itemsIds[i]) cartItems.push(item)
        }
    });

    const removeItem = function(e) {
        itemsIds = itemsIds.filter((item, index) => item != e.target.closest('li').id)
        document.cookie = `data=${itemsIds.join()}`;
        e.target.closest('li').remove();
        setCartNumber(getCookie('data').split(',').length);
    }

    const list = cartItems.map((item, index) => {
        return (
            <li id={item.id} key={index} className="item">
                <img src={item.image} className="cart_img"></img>
                    <div className="list_item_info">
                        <p className="info_title">{item.title}</p>
                        <p className="info_category">{item.category}</p>
                        <p className="info_price">${item.price}</p>
                    </div>
                    <div className="quantity">
                        <div className="quantity_field">1</div>
                        <button className="plus">+</button>
                        <button className="minus">-</button>
                    </div>
                    <button onClick={removeItem} className="item_remove">Remove from cart</button>
            </li>
        )
    })

    let cartEmpty = document.createElement('h1');
    cartEmpty.innerHTML = 'There is no goods in the cart...';

    let cartFull = document.createElement('h1');
    cartFull.innerHTML = 'Here are the products you have taken to the cart';

    return (
        <div className="cart">
            <h1 className="cart_header">Cart {cartState ? 'products are displayed below' : 'is empty'}</h1>
            <ul className="items_list">
                {list}
            </ul>
        </div>
    )
}

export default Cart;
