import {useContext} from 'react';
import {Context} from './App';

function Title() {
    const {sortStatus, setSortstatus} = useContext(Context);

    const sortProducts = function() {
        if(sortStatus != 'desc') {
            setSortstatus('desc') 
        } else {
            setSortstatus('asc')
        }
    }

    return (
        <div className="title">
            <h1>Best Sellers</h1>
            <button onClick={sortProducts} className="btn_sort">
                <span className="btn_sort_icon"></span>
            </button>
            <div className="slider_arrows">
                <button className="arrow_left">
                    <i className="arrow"></i>
                </button>
                <button className="arrow_right">
                    <i className="arrow"></i>
                </button>
            </div>
            <span className="title_accessories">Accessories</span>
        </div>
    )
}

export default Title;
