import { useContext, useEffect, useState } from 'react';
import {Context} from './App';

function Products() {
    const {dataStatus, sortStatus, setSortstatus, searchStatus, setSearchstatus} = useContext(Context);
    const [productsHtml, setProductsHtml] = useState('');
    let data = [];

    const sort = function(direction) {
        data = JSON.parse(localStorage.getItem('data'));
        data.sort((a, b) => {
            var nameA = a.title.toLowerCase(),
                nameB = b.title.toLowerCase();

            if(nameA < nameB) {
                return -1
            }    

            if(nameA > nameB) {
                return 1
            }

            return 0
        })
        
        if(direction == 'desc') data.reverse();

        show()

    }
    
    const search = function(query) {
        data = JSON.parse(localStorage.getItem('data'));
        data = data.filter(item => {
            if(item.title.indexOf(query) != -1) return item
        })

        show()
    }

    const show = function() {
        if(data.length == 0) data = JSON.parse(localStorage.getItem('data'));
        let list = data.map((item, index) => {
            return (
                <li key={index} className="list_item">
                    <img src={item.image}></img>
                    <div className="list_item_info">
                        <p className="info_title">{item.title}</p>
                        <p className="info_category">{item.category}</p>
                        <p className="info_price">${item.price}</p>
                        <button className="info_add_btn">+</button>
                    </div>
                </li>
            )
        })

        setProductsHtml(list)
    }

    useEffect(_ => {
        if(dataStatus) {
            show()
        }

        if(sortStatus != '') sort(sortStatus);

        if(searchStatus != '') {
            search(searchStatus)
        } else {
            show()
        }

    }, [dataStatus, sortStatus, searchStatus]);

    return (
        <ul className="list">
            {productsHtml}
        </ul>
    )
}

export default Products;
