import React, {useState, useEffect} from 'react';


function Cart() {
    let items = JSON.parse(localStorage.getItem('data'));

    function getCookie(data) {
        let itemsIds = document.cookie.match(new RegExp(
          "(?:^|; )" + data.replace(/([\.$?*|{}\(\)\[\]\\\/\+^])/g, '\\$1') + "=([^;]*)"
        ));
       
        return itemsIds ? decodeURIComponent(itemsIds[1]) : undefined;
    }

    let itemsIds = getCookie('data').split(',');

    let cartItems = [];

    items.forEach(item => {
        for(let i = 0; i < itemsIds.length; i++) {
            if(item.id == itemsIds[i]) cartItems.push(item)
        }
    });

    const list = cartItems.map((item, index) => {
        return (
            <li key={index}>
                <img src={item.image}></img>
                    <div className="list_item_info">
                        <p className="info_title">{item.title}</p>
                        <p className="info_category">{item.category}</p>
                        <p className="info_price">${item.price}</p>
                    </div>
            </li>
        )
    })

    return (
        <div className="cart">
            <h1>hey there i'm a cart guy</h1>
            <ul>
                {list}
            </ul>
        </div>
    )
}

export default Cart;
