import { useContext, useEffect, useState } from 'react';
import {Context} from './App';

function Products() {
    const {dataStatus, sortStatus, setSortstatus} = useContext(Context);
    const [productsHtml, setProductsHtml] = useState('');

    const sort = function() {
        let data = JSON.parse(localStorage.getItem('data'));
        data.sort((a, b) => {
            return +a.price - +b.price
        })

        console.log(data)
    }
    

    const show = function() {
        let data = JSON.parse(localStorage.getItem('data'));
        let list = data.map((item, index) => {
            return (
                <li key={index} className="list_item">
                    <img src={item.image}></img>
                    <div className="list_item_info">
                        <p className="info_title">{item.title}</p>
                        <p className="info_category">{item.category}</p>
                        <p className="info_price">${item.price}</p>
                        <button className="info_add_btn">+</button>
                    </div>
                </li>
            )
        })

        setProductsHtml(list)
    }

    useEffect(_ => {
        if(dataStatus) {
            show()
        }

        if(sortStatus != '') sort(sortStatus)

    }, [dataStatus, sortStatus]);

    return (
        <ul className="list">
            {productsHtml}
        </ul>
    )
}

export default Products;
