import { useContext, useEffect, useState } from 'react';
import {Context} from './App';

function Products() {
    const {dataStatus} = useContext(Context);
    const [productsHtml, setProductsHtml] = useState('');

    const show = function() {
        // 1) забрать данные из локасторэдж
        // 2) parse in object
        // 3) пройтись по данным через map
        // 3.1 функиця мэп должна вернуть ли каждого продукта с данными 
        let data = JSON.parse(localStorage.getItem('data'));
        console.log(data)
        let list = data.map((item) => {
            <li>{item}</li>
        })

        console.log(list)

        setProductsHtml(<li>123</li>)
    }

    useEffect(_ => {
        if(dataStatus) {
            show()
        }
    }, [dataStatus]);

    return (
        <ul>
            {productsHtml}
        </ul>
    )
}

export default Products;
