import {Context} from './App';
import {useContext} from 'react';
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link
} from "react-router-dom";

function Header() {
    const {setSearchstatus} = useContext(Context);


    const searchProducts = function(e) {
        if(e.target.value.length < 3) {
            setSearchstatus('')

            return
        }

        setSearchstatus(e.target.value)
    }

    const stopRedirection = function(e) {
        // e.preventDefault();
    }

    return (
        <header className="header">
            <div className="logo">
                <a href="/"><img src="./logo.png" alt="logo"/></a>
            </div>
            <div className="search_wrapper">
                <input onChange={searchProducts} type="text" name="search" placeholder="Search"/>
                <span className="search_icon"></span>
            </div>
            <Link onClick={stopRedirection} to="/cart" className="cart">
                <span className="cart_amount">0</span>
                <img src="cart.png" alt="cart"/>
            </Link>
        </header>
    )
}

export default Header;
