import {Context} from './App';
import {useContext, useState, useEffect} from 'react';

function Header() {
    const {searchStatus, setSearchstatus, cart} = useContext(Context);

    const [cartQuantity, setCartQuantity] = useState(0);

    const searchProducts = function(e) {
        if(e.target.value.length < 3) {
            setSearchstatus('')

            return
        }

        setSearchstatus(e.target.value)
    }

    useEffect(() => {
        setCartQuantity(1)
    }, [cart])

    return (
        <header className="header">
            <div className="logo">
                <a href="/"><img src="./logo.png" alt="logo"/></a>
            </div>
            <div className="search_wrapper">
                <input onChange={searchProducts} type="text" name="search" placeholder="Search"/>
                <span className="search_icon"></span>
            </div>
            <div className="cart">
                <span className="cart_amount">{cartQuantity}</span>
                <img src="cart.png" alt="cart"/>
            </div>
        </header>
    )
}

export default Header;
