import React, {useState, useEffect} from 'react';
import Header from './Header';
import Title from './Title';
import Catalog from './Catalog';
import Cart from './Cart';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";
import getCookie from './Products';

export const Context = React.createContext();

function App() {
  const [dataStatus, setDataStatus] = useState(false);
  const [sortStatus, setSortstatus] = useState('');
  const [searchStatus, setSearchstatus] = useState('');

  //
  const [itemId, setItemId] = useState();
  const [itemsIds, setItemsIds] = useState(getCookie('data') ? getCookie('data').split(',') : []);
  //

  const getData = function() {
    if(localStorage.getItem('data')) {
      if(!dataStatus) setDataStatus(true);

      return
    };

    fetch('https://fakestoreapi.com/products')
    .then(response => {
      return response.text()
    })
    .then(data => {
      localStorage.setItem('data', data);
      setDataStatus(true);
    })

  }

  getData()

  return (
    <React.Fragment>
      <Context.Provider value={{dataStatus, sortStatus, setSortstatus, searchStatus, setSearchstatus, itemId, setItemId, itemsIds, setItemsIds}}>
      <Router>
        <Header />
        {/* <Title /> */}
        <Switch>
          <Route exact path="/" component={Title}></Route>
        </Switch>
        <Switch>
          <Route exact path="/" component={Catalog}></Route>
        </Switch>
          <Switch>
            <Route exact path="/cart" component={Cart}></Route>
          </Switch>
      </Router>
      </Context.Provider>
    </React.Fragment>
  );
}

export default App;
