import {Context} from './App';
import {useContext, useState} from 'react';
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link
} from "react-router-dom";

function Header() {
    const {setSearchstatus} = useContext(Context);
    const [cartNumber, setCartNumber] = useState(getCookie('data') ? getCookie('data').split(',').length : 0)

    function getCookie(data) {
        let itemsIds = document.cookie.match(new RegExp(
          "(?:^|; )" + data.replace(/([\.$?*|{}\(\)\[\]\\\/\+^])/g, '\\$1') + "=([^;]*)"
        ));
       
        return itemsIds ? decodeURIComponent(itemsIds[1]) : undefined;
    }

    // let cookieLength = getCookie('data') ? getCookie('data').split(',').length : 0;
    // console.log(cookieLength)

    const searchProducts = function(e) {
        if(e.target.value.length < 3) {
            setSearchstatus('')

            return
        }

        setSearchstatus(e.target.value)
    }

    return (
        <header className="header">
            <div className="logo">
                <a href="/"><img src="./logo.png" alt="logo"/></a>
            </div>
            <div className="search_wrapper">
                <input onChange={searchProducts} type="text" name="search" placeholder="Search"/>
                <span className="search_icon"></span>
            </div>
            <Link to="/cart" className="cart">
                <span className="cart_amount">{cartNumber}</span>
                <img src="cart.png" alt="cart"/>
            </Link>
        </header>
    )
}

export default Header;
